mapper.insert:
INSERT INTO address
(user_id, address_name, address_status)
VALUES (#{userId}, #{addressName}, ${1});

mapper.update:
UPDATE address
SET is_deleted = ${0}
WHERE id = #{addressId};

UPDATE address
SET address_name = ${addressName}
WHERE id = #{id};

mapper.select:
SELECT id, user_id, address_name, address_status, create_time, update_time, is_deleted
FROM address
WHERE id = #{addressId};
