mapper.insert:
INSERT INTO cart
(user_id, commodity_id, address_id, commodity_num, order_status, is_deleted)
VALUES (#{userId}, #{commodityId}, #{addressId}, #{commodityNum}, #{orderStatus}, ${0});

mapper.update:
UPDATE cart
SET is_deleted = ${1}
WHERE id = #{cartId};

UPDATE cart
SET order_status = ${1}
WHERE id = #{id};
